CREATE VIEW `v_sa`  AS  select `a`.`idakun` AS `idakun`,`a`.`Debit` AS `debit`,`a`.`Kredit` AS `kredit`,`b`.`Induk` AS `induk` from (`t03_saldoawal` `a` left join `t02_akun` `b` on(`a`.`idakun` = `b`.`idakun`)) ;
CREATE VIEW `v_sa2`  AS  select `a`.`idakun` AS `idakun`,`a`.`debit` AS `debit`,`a`.`kredit` AS `kredit`,`b`.`Induk` AS `induk` from ((select `v_sa`.`induk` AS `idakun`,sum(`v_sa`.`debit`) AS `debit`,sum(`v_sa`.`kredit`) AS `kredit` from `v_sa` group by `v_sa`.`induk`) `a` left join `t02_akun` `b` on(`a`.`idakun` = `b`.`idakun`)) ;
CREATE VIEW `v_sa3`  AS  select `a`.`idakun` AS `idakun`,`a`.`debit` AS `debit`,`a`.`kredit` AS `kredit`,`b`.`Induk` AS `induk` from ((select `v_sa2`.`induk` AS `idakun`,sum(`v_sa2`.`debit`) AS `debit`,sum(`v_sa2`.`kredit`) AS `kredit` from `v_sa2` group by `v_sa2`.`induk`) `a` left join `t02_akun` `b` on(`a`.`idakun` = `b`.`idakun`)) ;
CREATE VIEW `v_sa4`  AS  select `a`.`idakun` AS `idakun`,`a`.`debit` AS `debit`,`a`.`kredit` AS `kredit`,`b`.`Induk` AS `induk` from ((select `v_sa3`.`induk` AS `idakun`,sum(`v_sa3`.`debit`) AS `debit`,sum(`v_sa3`.`kredit`) AS `kredit` from `v_sa3` group by `v_sa3`.`induk`) `a` left join `t02_akun` `b` on(`a`.`idakun` = `b`.`idakun`)) ;
CREATE VIEW `v_sa5`  AS  select `a`.`idakun` AS `idakun`,`a`.`debit` AS `debit`,`a`.`kredit` AS `kredit`,`b`.`Induk` AS `induk` from ((select `v_sa4`.`induk` AS `idakun`,sum(`v_sa4`.`debit`) AS `debit`,sum(`v_sa4`.`kredit`) AS `kredit` from `v_sa4` where `v_sa4`.`induk` <> 0 group by `v_sa4`.`induk`) `a` left join `t02_akun` `b` on(`a`.`idakun` = `b`.`idakun`)) ;
CREATE VIEW `v_bb`  AS  select `a`.`idakun` AS `idakun`,`a`.`Kode` AS `Kode`,`a`.`Nama` AS `Nama`,`a`.`Induk` AS `Induk`,`a`.`Urut` AS `Urut`,case when `b`.`debit` is null then 0 else `b`.`debit` end AS `Debit`,case when `b`.`kredit` is null then 0 else `b`.`kredit` end AS `Kredit` from (`t02_akun` `a` left join (select `a`.`idakun` AS `idakun`,sum(`a`.`debit`) AS `debit`,sum(`a`.`kredit`) AS `kredit` from (select `v_sa`.`idakun` AS `idakun`,`v_sa`.`debit` AS `debit`,`v_sa`.`kredit` AS `kredit`,`v_sa`.`induk` AS `induk` from `v_sa` union select `v_sa2`.`idakun` AS `idakun`,`v_sa2`.`debit` AS `debit`,`v_sa2`.`kredit` AS `kredit`,`v_sa2`.`induk` AS `induk` from `v_sa2` union select `v_sa3`.`idakun` AS `idakun`,`v_sa3`.`debit` AS `debit`,`v_sa3`.`kredit` AS `kredit`,`v_sa3`.`induk` AS `induk` from `v_sa3` union select `v_sa4`.`idakun` AS `idakun`,`v_sa4`.`debit` AS `debit`,`v_sa4`.`kredit` AS `kredit`,`v_sa4`.`induk` AS `induk` from `v_sa4` union select `v_sa5`.`idakun` AS `idakun`,`v_sa5`.`debit` AS `debit`,`v_sa5`.`kredit` AS `kredit`,`v_sa5`.`induk` AS `induk` from `v_sa5`) `a` group by `a`.`idakun`) `b` on(`a`.`idakun` = `b`.`idakun`)) ;